var HttpClientProvider = Java.type("org.keycloak.connections.httpclient.HttpClientProvider");
var Scanner = Java.type("java.util.Scanner");

var AUTH_URL_BASE = "http://user-api:8080/authorities/";

var httpClient = keycloakSession.getProvider(HttpClientProvider.class);

var inputStream = httpClient.get(AUTH_URL_BASE + user.username);
var scanner = new Scanner(inputStream, "UTF-8");

exports = scanner.useDelimiter("\\A").next();

